import React, {useEffect, useState} from 'react'
import Header1 from './Header.css'
import logo from '../Image/logo.png'
import { Navigate, useNavigate } from 'react-router-dom'
import axios from 'axios'
import { baseUrl } from './baseUrl'

function Header() { 
    useEffect(()=>{getCartData(); window.scrollTo(0,0)},[])
    const navigate = useNavigate()
    const [cartData, setCartData] = useState([]);
    const getCartData = () => {
      axios
        .get(baseUrl+"cart")
        .then((res) => setCartData(res.data.data));
    };

   const islogin= localStorage.getItem("user")


    const nevi=()=>{
        if(islogin==="yes"){
            navigate("/OrderHistory")
        }else{
            navigate("/Login")
        }
    }

    const [showhead, setShowhead] = useState(false)
    const [showinp, setShowInp] = useState(false)
    const [showinpres, setShowInpres] = useState(false)
    const emailInputRef = React.useRef(null);
    React.useEffect(()=>{
        setTimeout(()=>{  emailInputRef.current.focus();},200)
      }, [showinp]);
      const [input, setInput] = useState("")
 
  return (
    <div>
        {/* <div style={{width:"100%", height:"5vh", backgroundColor:"#18181A", color:"#ffffff",
         display:"flex", justifyContent:"center", alignItems:"center", fontSize:14}}>
            WORLDWIDE SHIPPING | COD AVAILABLE
        </div> */}
        {/* <div className='res-search'>
            <input placeholder='Search'/>
            <button style={{width:"15%", height:"4.5vh", border:"none", backgroundColor:"black", color:"white"}}>Search</button>
        </div> */}
        <div className='header'>
            {input}
            <div className='logo-div' style={{width:"30%"}}>
                    <img src={logo}  className="logo" onClick={()=>navigate("/")}/>
            </div>
            {/* <div className='head-search'>
                <input placeholder="Search"/>
                <i class="fa fa-search" style={{color:"#FFDAA9"}} aria-hidden="true"></i>
            </div> */} 
            <div style={{width:"40%", display:"flex", flexDirection:"column" }}>
                <div style={{width:"100%", cursor:'pointer', display:"flex" ,justifyContent:"center",alignItems:"center", gap:"5%" }}>
                <input onChange={(e)=>localStorage.setItem("searchInput", e.target.value)} ref={emailInputRef} style={{height:"30px", visibility:showinp?"":"hidden",width:showinp?"200px":"0px", transition:"0.3s",outline:"none", borderRadius:3}} placeholder="Search"/>
                <i onClick={()=>{
                    if(showinp===false){
                    setShowInp(true)}
                    else{
                        navigate('/Search')
                    }
                    }} class="fa fa-search web-icon-search" style={{color:"#FFDAA9"}} aria-hidden="true"></i>

                <i onClick={()=>setShowInpres(!showinpres)} class="fa fa-search res-icon-search" style={{color:"#FFDAA9"}} aria-hidden="true"></i>

                <i onClick={()=>nevi()} class="fa fa-user" style={{color:"#FFDAA9"}} aria-hidden="true"></i>
                <i class="fa fa-heart" style={{color:"#FFDAA9"}} aria-hidden="true"></i>
                {/* <div onClick={()=>navigate("/Wishlist")}  style={{width:40, height:40, backgroundColor:"black", borderRadius:"50%", marginLeft:"4%", display:"flex", justifyContent:"center", alignItems:"center", color:"white", position:"relative" }}> */}
                    {/* {cartData.length!==0&&<div style={{width:20,height:20,position:"absolute", top:0, backgroundColor:"black", right:2, borderRadius:"50%",display:"flex", justifyContent:"center", alignItems:"center",}}>{cartData.length}</div>} */}
                <i onClick={()=>navigate("/cart")} style={{color:"#FFDAA9"}} class="fa fa-shopping-bag" aria-hidden="true"></i>
                <i  onClick={()=>setShowhead(!showhead)} style={{color:"#FFDAA9"}} class="fa fa-bars nav-icon" aria-hidden="true"></i>
                </div>
                <div className='res-search-input'>
                <input ref={emailInputRef} style={{height:"30px", visibility:showinpres?"":"hidden",width:showinpres?"180px":"0px", transition:"0.3s",outline:"none", borderRadius:3}} placeholder="Search"/>
                </div>
            </div>
        </div>
        <div className='head-category'>
            <div onClick={()=>navigate("/NewArrival")} style={{textAlign:"center", fontSize:11}}><span>New Arrival</span></div>
            <div onClick={()=>navigate("/Saree")} style={{textAlign:"center", fontSize:11}}><span>Sarees</span></div>
            <div onClick={()=>navigate("/")} style={{textAlign:"center", fontSize:11}}><span>Coordinate</span></div>
            <div onClick={()=>navigate("/Jumpsuit")} style={{textAlign:"center", fontSize:11}}><span>Jumpsuit</span></div>
            <div onClick={()=>navigate("/Lehanga")} style={{textAlign:"center", fontSize:11}}><span>Lehenga</span></div>
            <div onClick={()=>navigate("/Gowns")} style={{textAlign:"center", fontSize:11}}><span>Gowns</span></div>
            <div onClick={()=>navigate("/Kaftan")} style={{textAlign:"center", fontSize:11}}><span>Kaftan</span></div>
            <div onClick={()=>navigate("/Anarkali")} style={{textAlign:"center", fontSize:11}}><span>Anarkali</span></div>
            <div onClick={()=>navigate("/Celebrities")} style={{textAlign:"center", fontSize:11}}><span>Celebrities</span></div>
            <div onClick={()=>navigate("/Bestseller")} style={{textAlign:"center", fontSize:11}}><span>Bestsellers</span></div>
            {/* <div onClick={()=>navigate("/")} style={{width:"8%", textAlign:"center"}}><label>swishboss</label></div> */}
        </div>

        {/* RESPONSIVE HEAD */}
        <div className='res-category' style={{width:"100%", height:showhead===true?400:0, display:"flex", overflow:"hidden",  flexDirection:"column",justifyContent:"space-evenly" ,alignItems:"center",transition:"1s",textTransform:"uppercase"}}>
            <div onClick={()=>navigate("/NewArrival")} ><label>New Arrival</label></div>
            <div onClick={()=>navigate("/Saree")} ><label>Sarees</label></div>
            <div onClick={()=>navigate("/")} ><label>Coordinate</label></div>
            <div onClick={()=>navigate("/Jumpsuit")} ><label>Jumpsuit</label></div>
            <div onClick={()=>navigate("/Lehanga")} ><label>Lehenga</label></div>
            <div onClick={()=>navigate("/Gowns")} ><label>Gowns</label></div>
            <div onClick={()=>navigate("/Kaftan")} ><label>Kaftan</label></div>
            <div onClick={()=>navigate("/Anarkali")} ><label>Anarkali</label></div>
            <div onClick={()=>navigate("/Celebrities")}><label>Celebrities</label></div>
            <div onClick={()=>navigate("/Bestseller")} ><label>Bestsellers</label></div>
            {/* <div onClick={()=>navigate("/")} style={{width:"8%", textAlign:"center"}}><label>swishboss</label></div> */}
        </div>
    </div> 
  )
}

export default Header
