import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { baseUrl } from "./baseUrl";
import Footer from "./Footer";
import Header from "./Header";
import cart from './cart.css'

function Cart() {
  useEffect(() => {
    getCartData(); window.scrollTo(0,0)
  }, []);
  const navigate = useNavigate();
  const [cartData, setCartData] = useState([]);
  const totalPrice = cartData.map((i) => i.price*i.quantity).reduce((a, b) => a + b, 0);
  const getCartData = () => {
    axios
      .get(baseUrl+"cart")
      .then((res) => setCartData(res.data.data));
  };
  const deleteCartData = (x) => {
    axios
      .delete(baseUrl+"cart/" + x)
      .then(() => getCartData());
  };
  const updateData = (x,y)=>{
    axios.put(baseUrl+"cart/" + x,{
        quantity:y
    }).then(()=>getCartData())
  }
  return (
    <div>
      <Header />
      <div
        style={{
          width: "100%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <b style={{ fontSize: 40, fontWeight: "lighter" }}>Your Cart</b>
        <div
          style={{
            borderBottom: "1px solid black",
            width: "11%",
            textAlign: "center",
          }}
        >
          <label onClick={()=>navigate("/")} style={{cursor:"pointer"}}>Continue Shopping</label>
        </div>
      </div>
      <br />
      <div style={{ width: "90%", marginLeft: "5%" }}>
        <table className="table">
          <thead>
            <th style={{ width: "50%" }}>Product</th>
            <th className="cart-title" style={{ textAlign: "center" }}>Price</th>
            <th>Quantity</th>
            <th style={{ textAlign: "end" }}>Total</th>
          </thead>
          <tbody>
            {cartData.map((i) => (
              <tr>
                <td style={{ width: "50%" }}>
                  <div style={{ width: "100%", display: "flex" }}>
                    <div style={{ width: "20%" }}>
                      <img style={{ width: "100%" }} src={i?.thumbnailImage} />
                    </div>
                    <div 
                      style={{
                        display: "flex",
                        flexDirection: "column",
                        marginLeft: "5%",
                      }}
                    >
                      <label className="cart-title">{i.title}</label>
                      <b>{i?.brand}</b>
                      <label>Size : {i?.size}</label>
                      <label
                        style={{ textDecoration: "underline" }}
                        onClick={() => deleteCartData(i._id)}
                      >
                        Remove
                      </label>
                    </div>
                  </div>
                </td>
                <td className="cart-title" style={{ width: "20%", textAlign: "center", fontWeight:"bold" }}>
                  Rs.{i?.price}
                </td>
                <td style={{ width: "10%" }}>
                <button   style={{ width: 30, height: 30, borderRadius:"50%", border:"none"}} onClick={()=>{
                    updateData(i._id,i?.quantity-1)
                    setCartData(cartData.map((j)=>j._id===i.Id?{...j,quantity:i?.quantity-1}:i))
                  }
                }>-</button>
                  <label style={{ width: "30%", height: "6vh" }}>
                    {i?.quantity}
                  </label>
                  <button  style={{ width: 30, height: 30, borderRadius:"50%", border:"none"}} onClick={()=>{
                    updateData(i._id,i?.quantity+1)
                    setCartData(cartData.map((j)=>j._id===i.Id?{...j,quantity:i?.quantity+1}:i))
                  }
                }>+</button>
                </td>
                <td style={{ width: "20%", textAlign: "end",  fontWeight:"bold" }}>
                  Rs.{i?.quantity*i?.price}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div style={{width:"100%",display:"flex", flexDirection:"column", alignItems:"end", paddingRight:"5%"}}>
        <div style={{width:"30%",display:"flex", justifyContent:"space-between", fontSize:18}}>
        <label>Subtotal:</label>
        <b>Rs.{totalPrice}</b>
        </div>
        <div >
        <label>Taxes and shipping calculated at checkout</label>
        </div>
        <button onClick={()=>navigate("/Checkout")} style={{width:"10%", height:"6vh", border:"none", color:"white", backgroundColor:"black", }}>CHECK OUT</button>
      </div>
      <br/>
      <Footer />
    </div>
  );
}

export default Cart;
