import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";
import Header from "./Header";

function Cart() {
  useEffect(() => {
    getCartData();
  }, []);
  const navigate = useNavigate();
  const [cartData, setCartData] = useState([]);
  const totalPrice = cartData.map((i) => i.price).reduce((a, b) => a + b, 0);
  const getCartData = () => {
    axios
      .get("http://localhost:5001/api/cart")
      .then((res) => setCartData(res.data.data));
  };
  const deleteCartData = (x) => {
    axios
      .delete("http://localhost:5001/api/cart/" + x)
      .then(() => getCartData());
  };
  const updateData = (x,y)=>{
    axios.put("http://localhost:5001/api/cart/" + x,{
        quantity:y
    }).then(()=>getCartData())
  }
  return (
    <div>
      <Header />
      <div
        style={{
          width: "100%",
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <b style={{ fontSize: 40, fontWeight: "lighter" }}>Your Cart</b>
        <div
          style={{
            borderBottom: "1px solid black",
            width: "11%",
            textAlign: "center",
          }}
        >
          <label onClick={()=>navigate("/")} style={{cursor:"pointer"}}>Continue Shopping</label>
        </div>
      </div>
      <br />
      <div style={{ width: "90%", marginLeft: "5%" }}>
        <table className="table">
          <thead>
            <th style={{ width: "50%" }}>Product</th>
            <th style={{ textAlign: "center" }}>Price</th>
            <th>Quality</th>
            <th style={{ textAlign: "end" }}>Total</th>
          </thead>
          <tbody>
            {cartData.map((i) => (
              <tr>
                <td style={{ width: "50%" }}>
                  <div style={{ width: "100%", display: "flex" }}>
                    <div style={{ width: "20%" }}>
                      <img style={{ width: "100%" }} src={i?.thumbnailImage} />
                    </div>
                    <div
                      style={{
                        display: "flex",
                        flexDirection: "column",
                        marginLeft: "5%",
                      }}
                    >
                      <label>{i.title}</label>
                      <b>{i?.brand}</b>
                      <label>Size : {i?.size}</label>
                      <label
                        style={{ textDecoration: "underline" }}
                        onClick={() => deleteCartData(i._id)}
                      >
                        Remove
                      </label>
                    </div>
                  </div>
                </td>
                <td style={{ width: "20%", textAlign: "center" }}>
                  Rs.{i?.price}
                </td>
                <td style={{ width: "10%" }}>
                  <input
                  onChange={(e)=>{
                    updateData(i._id,e.target.value)
                    setCartData(cartData.map((j)=>j._id===i.Id?{...j,quantity:e}:i))
                  }
                }
                    type="number"
                    defaultValue="1"
                    min="1"
                    value={i?.quantity}
                    style={{ width: "60%", height: "6vh" }}
                  />
                </td>
                <td style={{ width: "20%", textAlign: "end" }}>
                  Rs.{i?.quantity*i?.price}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div style={{width:"100%",display:"flex", flexDirection:"column", alignItems:"end", paddingRight:"5%"}}>
        <div style={{width:"20%",display:"flex", justifyContent:"space-between", fontSize:18}}>
        <label>Subtotal:</label>
        <b>Rs.{totalPrice}</b>
        </div>
        <div >
        <label>Taxes and shipping calculated at checkout</label>
        </div>
        <button style={{width:"10%", height:"6vh", border:"none", color:"white", backgroundColor:"black", }}>CHECK OUT</button>
      </div>
      <br/>
      <Footer />
    </div>
  );
}

export default Cart;
